CREATE OR REPLACE PROCEDURE ARQFACTSQA.PRC_CREATE_TRAZA(iVaPrcPkgName in varchar2,
							   iVaPrcPkgErrMsg in varchar2,
							   iClPayload in clob)
    IS

	BEGIN
		insert into TRAZA_PROCESS(PKG_PRC_NAME, ERR_MSG, PROCESS_PAYLOAD)
		values (iVaPrcPkgName, iVaPrcPkgErrMsg, iClPayload);
        COMMIT;
    EXCEPTION
        WHEN OTHERS THEN
            DBMS_OUTPUT.PUT_LINE('Ha ocurrido una excepcion al intentar crear traza... ' || SQLERRM);

	END PRC_CREATE_TRAZA;
